sources.sass\\util\\breakpoints.scss:
// 640px, 1150x, 1400px
$breakpoints-up: (
    "medium": '40em',
    "large": '71.875em',
    "xlarge": '87.5em'
);

// 639.98px, 1149.98px, 1439.98px
$breakpoints-down: (
    'small': '39.9375em',
    'medium': '71.87375em',
    'large': '87.4375em'
);

@mixin breakpoint($size) {
    @media only screen and (min-width: map-get($breakpoints-up, $size)) {
        @content;
    }
}

@mixin breakpoint-down($size) {
    @media only screen and (max-width: map-get($breakpoints-down, $size)) {
        @content;
    }
}

